                delLocalValue(`mgcAttach_step5`);
                request(`xsrf_token=${esgst.xsrfToken}&do=reopen_discussion`, null, `POST`, false, location.href, function () {
                    setLocalValue(`mgcAttach_step6`, true);
                    location.reload();
                });
